INSERT INTO `about` (`info_id`, `info`) VALUES
(1, 'Мы команда программистов которая очень любит играть в Dungeons & Dragons. Многие игроки D&D часто сталкиваются с проблемой во времени или положении. Например, не редко случается так, что игроки хотят устроить кампанию, но, к примеру, игрокам не хватает времени, или лучшие друзья живут в разных городах, и им удаётся поиграть очень редко. Также может случиться такое, что в последний момент игра отменяется из-за проблем или работы. <br>\nЦель нашего проекта – дать Вам возможность играть в любимую ролевую игру онлайн без лишней долгой подготовки, конечно, если не считать создание персонажа, которое легко подстроено под пользователя и с удобным интерфейсом. Но даже играя в <s>нашу</s> Вашу игру не забывайте встречаться в реальности и играть со своими друзьями, ведь любая живая встреча лучше компьютерной. Желаем вам удачи!');
INSERT INTO `armor` (`armor_id`, `armor_name`, `armor_type`, `armor_cost`, `armor_ac`, `armor_str`, `armor_stealth`, `armor_weight`, `armor_don`, `armor_doff`, `armor_discription`) VALUES
(1, 'Стёганый доспех', 'Лёгкий', 5, 11, 0, 'Помеха', 8, 10, 10, 'Стёганый доспех состоит из прошитых слоёв ткани и ватина.'),
(2, 'Кожаный доспех', 'Лёгкий', 10, 11, 0, '', 10, 10, 10, 'Нагрудник и плечи этого доспеха изготовлены из кожи, вываренной в масле. Остальные части доспеха сделаны из более мягких и гибких материалов.'),
(3, 'Клёпанный кожаный доспех', 'Лёгкий', 45, 12, 0, '', 13, 10, 10, 'Изготовленный из крепкой, но гибкой кожи проклёпанный доспех усилен тесно расположенными шипами или заклёпками.'),
(4, 'Шкурный доспех', 'Средний', 10, 12, 0, '', 12, 50, 10, 'Этот грубый доспех состоит из толстых мехов и шкур. Обычно их носят племена варваров, злые гуманоиды и прочие народы, у которых нет инструментов и материалов для создания более качественных доспехов.'),
(5, 'Кольчужная рубаха', 'Средний', 50, 13, 0, '', 20, 50, 10, 'Сделанная из переплетённых металлических колец кольчужная рубаха носится между слоями одежды или кожи. Этот доспех предоставляет умеренную защиту торса и заглушает звон колец внешним покрытием.'),
(6, 'Чешуйчатый доспех', 'Средний', 50, 14, 0, 'Помеха', 45, 50, 10, 'Этот доспех состоит из кожаных куртки и поножей (а также, возможно, отдельной юбки), покрытых перекрывающимися кусочками металла, похожими на рыбную чешую. В комплект входят рукавицы.'),
(7, 'Кираса', 'Средний', 400, 14, 0, '', 20, 50, 10, 'Этот доспех состоит из подогнанного металлического панциря, носимого с подкладкой из кожи. Несмотря на то, что руки и ноги остаются практически без защиты, этот доспех хорошо защищает жизненно важные органы, оставляя владельцу относительную подвижность.'),
(8, 'Полулаты', 'Средний', 750, 15, 0, 'Помеха', 40, 50, 10, 'Полулаты состоят из сформированных металлических пластин, покрывающих большую часть тела владельца. В них не входит защита для ног кроме простых поножей, закреплённых кожаными ремнями.'),
(9, 'Колечный', 'Тяжёлый', 30, 14, 0, 'Помеха', 40, 100, 50, 'Это кожаный доспех с нашитыми на него толстыми кольцами. Эти кольца усиливают доспех от ударов мечей и топоров. Колечный доспех хуже кольчуги, и обычно его носят только те, кто не могут позволить себе доспех получше.'),
(10, 'Кольчуга', 'Тяжёлый', 75, 16, 13, 'Помеха', 55, 100, 50, 'Изготовленная из переплетающихся металлических колец кольчуга включает также слой стёганой ткани, надеваемой под низ, дабы предотвратить натирание и смягчать удары. В комплект входят рукавицы.'),
(11, 'Наборный доспех', 'Тяжёлый', 200, 17, 15, 'Помеха', 60, 100, 50, 'Этот доспех состоит из узких вертикальных металлических пластин, приклёпанных к кожаной подложке, носимой поверх слоя ватина. Соединения защищаются кольчужным полотном.'),
(12, 'Латы', 'Тяжёлый', 1500, 18, 15, 'Помеха', 65, 100, 50, 'Латы состоят из сформированных металлических пластин, покрывающих всё тело. В комплект лат входят рукавицы, тяжёлые кожаные сапоги, шлем с забралом, и толстый слой ватина. Ремешки и пряжки распределяют вес по всему телу.\r\n\r\n'),
(13, 'Щит', 'Щит', 10, 2, 0, '', 6, 1, 1, 'Щит изготавливается из дерева или металла, и несётся одной рукой. Использование щита увеличивает КД на 2. Вы получаете преимущество только от одного щита одновременно.');
